import React, { useState } from 'react';
import './Profilepage.css';

const ProfilePage = () => {
  const [name, setName] = useState('');
  const [field1, setField1] = useState('');
  const [field2, setField2] = useState('');
  // ... other field states

  const handleSubmit = (e) => {
    e.preventDefault();
    // Validate form data here
    if (!name || !field1 || !field2) {
      alert('Please fill in all required fields.');
      return;
    }

    // Save data to local storage or database
    localStorage.setItem('profileData', JSON.stringify({ name, field1, field2, /* ... other fields */ }));
    alert('Data saved successfully!');
  };

  const handleClear = () => {
    setName('');
    setField1('');
    setField2('');
    // ... clear other fields
    alert('Data cleared!');
  };

  return (
    <div className="profile-page">
      <div className="background-image-container">
        <img src="npc_hackathon/public/bg.jpg" alt="Baground" />
      </div>
      <div className="form-container">
        <h1>Profile</h1>
        <form onSubmit={handleSubmit}>
          <div className="form-group">
            <label htmlFor="name">Name:</label>
            <input type="text" id="name" value={name} onChange={(e) => setName(e.target.value)} required />
          </div>
          <div className="form-group">
            <label htmlFor="field1">Field 1:</label>
            <input type="text" id="field1" value={field1} onChange={(e) => setField1(e.target.value)} required />
          </div>
          <div className="form-group">
            <label htmlFor="field2">Field 2:</label>
            <input type="text" id="field2" value={field2} onChange={(e) => setField2(e.target.value)} required />
          </div>
          {/* ... other form fields */}
          <div className="form-group">
            <button type="submit">Save Changes</button>
            <button type="button" onClick={handleClear}>Clear Details</button>
          </div>
        </form>
      </div>
    </div>
  );
};

export default ProfilePage;
